import turtle
tina=turtle.Turtle()

def make_cake():
	tina.pensize(10)
	tina.color("yellow")
	tina.right(90)
	tina.forward(20)
	tina.color('black')
	tina.forward(45)
	tina.color('pink')
	tina.left(90)
	tina.forward(50)
	tina.right(90)
	tina.forward(50)
	tina.right(90)
	tina.forward(100)
	tina.right(90)
	tina.forward(50)
	tina.right(90)
	tina.forward(50)

make_cake()


turtle.done()
